import React, { Component } from "react";
import Navigation from "./Navigation";
import { Spinner, Button, Input, Container } from "reactstrap";

export default class Category extends Component {
  state = {
    isLoading: true,
    Categories: [],
  };
  async componentDidMount() {
    const response = await fetch("/api/categories");
    const body = await response.json();
    this.setState({ Categories: body, isLoading: false });
  }

  render() {
    const { Categories, isLoading } = this.state;
    if (isLoading)
      return (
        <div>
          <Navigation />
          <Spinner className="centered-spinner" color="primary" />
        </div>
      );

    return (
      <Container>
        <Navigation />

        <h2>Преглед на Категории</h2>
        {Categories.map((category) => (
          <div key={category.id} id={category.id}>
            {category.name}
          </div>
        ))}

        <Container>
          <h3>Додај категорија</h3>
          <Input type="text" placeholder="Впиши категорија" />
          <Button
            className="col"
            className="btn btn-sm -sm"
            outline
            color="success"
          >
            Зачувај
          </Button>{" "}
        </Container>
        <Container>
          <h3>Избриши категорија</h3>
        </Container>
      </Container>
    );
  }
}
