import React, { Component } from "react";
import Navigation from "./Navigation";
import { Spinner, Button, Input, Container } from "reactstrap";

export default class Category extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoading: true,
      Categories: [],
    };
  }
  async componentDidMount() {
    const response = await fetch("/api/categories");
    const body = await response.json();
    this.setState({ Categories: body, isLoading: false });
  }

  render() {
    const { Categories, isLoading } = this.state;
    if (isLoading)
      return (
        <div>
          <Navigation />
          <Spinner className="centered-spinner" color="primary" />
        </div>
      );

    {
      Categories.map((category) => (
        <div key={category.id} id={category.id}>
          {category.name}
        </div>
      ));
    }

    let rows = Categories.map((category) => (
      <tr key={category.id}>
        <td>{category.name}</td>
        <td>
          <Button
            size="sm"
            color="danger"
            onClick={() => this.remove(category.id)}
          >
            Избриши
          </Button>
        </td>
      </tr>
    ));

    return (
      <>
        <Navigation />
        <Container>
          <h2>Преглед на Категории</h2>
        </Container>
        <Container>
          <h3>Додај категорија</h3>
          <Input className="w-25" type="text" placeholder="Впиши категорија" />
          <Button className="btn btn-sm -sm" outline color="success">
            Зачувај
          </Button>{" "}
        </Container>
        <Container>
          <h3>Избриши категорија</h3>
        </Container>
      </>
    );
  }
}
