import React, { Component } from "react";
import Navigation from "./Navigation";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Moment from "react-moment";
import {
  Table,
  Container,
  Form,
  FormGroup,
  Button,
  Input,
  Label,
  Spinner,
} from "reactstrap";
import { Link } from "react-router-dom";

export default class Bill extends Component {
  emptyItem = {
    id: 1,
    date: new Date(),
    description: "",
    price: 0.74,
    category: [2, "Kategorija2"],
  };
  constructor(props) {
    super(props);
    this.state = {
      show: false,
      date: new Date(),
      isLoading: false,
      bills: [],
      categories: [],
      item: this.emptyItem,
      id: 0,
      description: "",
      price: 0,
      category: [2, "Kategorija2"],
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleDateChange = this.handleDateChange.bind(this);
  }
  handleDateChange(date) {
    let item = { ...this.state.item };
    item.date = date;
    this.setState({ item });
    console.log(item);
  }

  async handleSubmit(event) {
    const item = this.state.item;

    await fetch(`/api/bills`, {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(item),
    });

    event.preventDefault();
    this.props.history.push("/bills");
  }

  handleChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    let item = { ...this.state.item };
    item[name] = value;
    this.setState({ item });
    console.log(this.state);
  }
  async remove(id) {
    await fetch(`api/bills/${id}`, {
      method: "DELETE",
      headers: {
        Accept: "application/json",
        "Content-Type": "applicaton/json",
      },
    }).then(() => {
      let updatedBills = [...this.state.bills].filter((i) => i.id !== id);
      this.setState({ bills: updatedBills });
    });
  }

  async componentDidMount() {
    const response = await fetch("api/categories");
    const body = await response.json();
    this.setState({ categories: body, isLoading: false });

    const responseBill = await fetch("api/bills");
    const bodyBill = await responseBill.json();
    this.setState({ bills: bodyBill, isLoading: false });
  }

  render() {
    const title = <h3>Додај сметка за наплата</h3>;
    const { categories } = this.state;
    const { bills, isLoading } = this.state;

    if (isLoading)
      return (
        <div>
          <Spinner className="centered-spinner" color="primary" />
        </div>
      );
    let categoryList = categories.map((category) => (
      <option key={category.id}>{category.name}</option>
    ));
    let rows = bills.map((bill) => (
      <tr key={bill.id}>
        <td>{bill.description}</td>
        <td>{bill.price}</td>
        <td>
          <Moment date={bill.date} format="YYYY-MM-DD"></Moment>
        </td>
        <td>{bill.category.name}</td>
        <td>
          <Button size="sm" color="danger" onClick={() => this.remove(bill.id)}>
            Избриши
          </Button>
        </td>
      </tr>
    ));

    return (
      <div>
        <Navigation />
        <Button
          className=" float-xs-right ml-5 ml-lg-0 text-center"
          onClick={() => {
            this.setState({ show: !this.state.show });
          }}
        >
          {this.state.show
            ? "Сокриј прозорец за додавање сметки"
            : "Додади сметки"}
        </Button>

        {this.state.show ? (
          <Container>
            <Form>
              {title}
              <FormGroup>
                <Label htmlFor="title">Наслов</Label>
                <Input
                  type="text"
                  name="description"
                  id="description"
                  onChange={this.handleChange}
                />
              </FormGroup>
              <FormGroup>
                <Label htmlFor="category">Категорија</Label>
                <select onChange={this.handleChange}>{categoryList}</select>
              </FormGroup>

              <FormGroup>
                <Label htmlFor="date">Датум</Label>
                <DatePicker
                  selected={this.state.item.date}
                  onChange={this.handleDateChange}
                />
              </FormGroup>

              <FormGroup>
                <Label htmlFor="price">Цена</Label>
                <Input type="number" onChange={this.handleChange} />
              </FormGroup>

              <FormGroup>
                <Button color="primary" type="submit">
                  Зачувај
                </Button>{" "}
                <Button color="secondary" tag={Link} to="/categories">
                  Откажи
                </Button>{" "}
              </FormGroup>
            </Form>
          </Container>
        ) : null}

        <Container>
          <h3>Листа за наплата</h3>
          <Table className="mt-4" dark>
            <thead>
              <tr>
                <th width="20%">Опис</th>
                <th width="10%">Цена</th>
                <th width="10%">Датум</th>
                <th width="10%">Категорија</th>
                <th width="10%">Опции</th>
              </tr>
            </thead>
            <tbody>{rows}</tbody>
          </Table>
        </Container>
      </div>
    );
  }
}
