import React, { Component } from "react";
import Navigation from "./Navigation";
import { Spinner, Button, Input, Container, Table } from "reactstrap";

export default class Category extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoading: true,
      Categories: [],
    };
  }
  async componentDidMount() {
    const response = await fetch("/api/categories");
    const body = await response.json();
    this.setState({ Categories: body, isLoading: false });
  }

  async remove(id) {
    await fetch(`api/category/${id}`, {
      method: "DELETE",
      headers: {
        Accept: "application/json",
        "Cotent-Type": "application/json,",
      },
    }).then(() => {
      let updatedCategories = [...this.state.Categories].filter(
        (i) => i.id != id
      );
      this.setState({ Categories: updatedCategories });
    });
  }

  render() {
    const { Categories, isLoading } = this.state;
    if (isLoading)
      return (
        <div>
          <Navigation />
          <Spinner className="centered-spinner" color="primary" />
        </div>
      );
    let rows = Categories.map((category) => (
      <tr key={category.id}>
        <td>{category.name}</td>
        <td>
          <Button
            size="sm"
            color="danger"
            onClick={() => this.remove(category.id)}
          >
            x
          </Button>
        </td>
      </tr>
    ));
    return (
      <>
        <Navigation />
        <Container className="flex">
          <h3>Додај категорија</h3>
          <Input
            className="w-25"
            type="text"
            placeholder="Впиши категорија"
          />{" "}
          <Button className="row btn btn-sm -sm">Зачувај</Button>{" "}
        </Container>
        <Container>
          <h3>Преглед на Категории</h3>
          <Table className="mt-4" dark>
            <thead>
              <tr>
                <th width="95%">Опис</th>
                <th>Опции</th>
              </tr>
            </thead>
            <tbody>{rows}</tbody>
          </Table>
        </Container>
      </>
    );
  }
}
