import React, { Component } from "react";
import Navigation from "./Navigation";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import {
  Table,
  Container,
  Form,
  FormGroup,
  Button,
  Input,
  Label,
} from "reactstrap";
import { Link } from "react-router-dom";
export default class Bill extends Component {
  emptyItem = {
    id: 1,
    date: new Date(),
    description: "",
    price: 0.74,
    category: [2, "Kategorija2"],
  };
  constructor(props) {
    super(props);
    this.state = {
      date: new Date(),
      isLoading: true,
      bills: [],
      categories: [],
      item: this.emptyItem,
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.hand;
  }
  handleDate(date) {
    let item = { ...this.state.item };
    item.date = date;
    this.setState({ item });
  }
  async handleSubmit(event) {
    event.preventDefault();
    const { item } = this.state;
    await fetch("api/bills", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(item),
    });
    console.log(this.state);
    this.props.history.push("/bills");
  }

  handleChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    let item = { ...this.state.item };
    item[name] = value;
    this.setState({ item });
    console.log(this.state);
  }
  async remove(id) {
    await fetch(`api/bills/${id}`, {
      method: "DELETE",
      headers: {
        Accept: "application/json",
        "Content-Type": "applicaton/json",
      },
    }).then(() => {
      let updatedBills = [...this.state.bills].filter((i) => i.id !== id);
      this.setState({ bills: updatedBills });
    });
  }

  async componentDidMount() {
    const response = await fetch("api/categories");
    const body = await response.json();
    this.setState({ categories: body, isLoading: false });

    const responseBill = await fetch("api/bills");
    const bodyBill = await responseBill.json();
    this.setState({ bills: bodyBill, isLoading: false });
  }

  render() {
    const title = <h3>Add Bill</h3>;
    // const { bills, isLoading } = this.state;
    const { categories } = this.state;
    const { bills, isLoading } = this.state;

    if (isLoading) return <div>Loading...</div>;
    let categoryList = categories.map((category) => (
      <option key={category.id}>{category.name}</option>
    ));
    let rows = bills.map((bill) => (
      <tr key={bill.id}>
        <td>{bill.description}</td>
        <td>{bill.price}</td>
        <td>{bill.date}</td>
        <td>{bill.category.name}</td>
        <td>
          <Button size="sm" color="danger" onClick={() => this.remove(bill.id)}>
            Delete
          </Button>
        </td>
      </tr>
    ));

    return (
      <div>
        <Navigation />
        <Container>
          <Form>
            {title}
            <FormGroup>
              <Label htmlFor="title">Title</Label>
              <Input
                type="text"
                name="title"
                id="title"
                onChange={this.handleChange}
              />
            </FormGroup>
            <FormGroup>
              <Label htmlFor="category">Category</Label>
              <select>{categoryList}</select>
              <Input
                type="text"
                name="category"
                id="category"
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <Label htmlFor="date">Date</Label>
              <DatePicker
                selected={this.state.date}
                onChange={this.handleDate}
              />
            </FormGroup>

            <FormGroup>
              <Label htmlFor="price">Price</Label>
              <Input
                type="text"
                name="price"
                id="price"
                type="number"
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <Button color="primary" type="submit">
                Save
              </Button>{" "}
              <Button color="secondary" tag={Link} to="/categories">
                Cancel
              </Button>{" "}
            </FormGroup>
          </Form>
        </Container>

        <Container>
          <h3>Bill List</h3>
          <Table className="mt-4">
            <thead>
              <tr>
                <th width="20%">Description</th>
                <th width="10%">Price</th>
                <th width="10%">Date</th>
                <th width="10%">Category</th>
                <th width="10%">Action</th>
              </tr>
            </thead>
            <tbody>{rows}</tbody>
          </Table>
        </Container>
      </div>
    );
  }
}
