import React, { Component } from "react";
import Navigation from "./Navigation";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { Table, Container, Form, FormGroup, Button, Input } from "reactstrap";
import { Link } from "react-router-dom";
export default class Bill extends Component {
  emptyItem = {
    id: 1,
    date: new Date(),
    description: "",
    price: 0.74,
    category: [2, "Kategorija2"],
  };
  constructor(props) {
    super(props);
    this.state = {
      date: new Date(),
      isLoading: true,
      bills: [],
      categories: [],
      item: this.emptyItem,
    };
  }

  async componentDidMount() {
    const response = await fetch("api/categories");
    const body = await response.json();
    this.setState({ categories: body, isLoading: false });

    const responseBill = await fetch("api/bills");
    const bodyBill = await responseBill.json();
    this.setState({ bills: bodyBill, isLoading: false });
  }

  render() {
    const title = <h3>Add Bill</h3>;
    // const { bills, isLoading } = this.state;
    const { categories } = this.state;
    const { bills, isLoading } = this.state;

    if (isLoading) return <div>Loading...</div>;
    let categoryList = categories.map((category) => (
      <option id={category.id}>{category.name}</option>
    ));

    return (
      <div>
        <Navigation />
        <Container>
          <Form>
            {title}
            <FormGroup>
              <label for="title">Title</label>
              <Input
                type="text"
                name="title"
                id="title"
                onChange={this.handleChange}
              />
            </FormGroup>
            <FormGroup>
              <label for="category">Category</label>
              <select>{categoryList}</select>
              <Input
                type="text"
                name="category"
                id="category"
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <label for="date">Date</label>
              <DatePicker
                selected={this.state.date}
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <label for="price">Price</label>
              <Input
                type="text"
                name="price"
                id="price"
                type="number"
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <Button color="primary" type="submit">
                Save
              </Button>{" "}
              <Button color="secondary" tag={Link} to="/categories">
                Cancel
              </Button>{" "}
            </FormGroup>
          </Form>
        </Container>

        <Container>
          <h3>Bill List</h3>
          <Table className="mt-4">
            <thead>
              <tr>
                <th width="20%">Description</th>
                <th width="10%">Price</th>
                <th width="10%">Category</th>
                <th width="10%">Action</th>
              </tr>
            </thead>
          </Table>
        </Container>
      </div>
    );
  }
}
