import React, { Component } from "react";
import Navigation from "./Navigation";
import {
  UncontrolledAlert,
  Input,
  Button,
  Badge,
  Row,
  Container,
  Spinner,
} from "reactstrap";
export default class Todo extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [],
      title: "",
      status: "false",
    };
    this.addItem = this.addItem.bind(this);
    this.changeStatus = this.changeStatus.bind(this);
  }

  async changeStatus(id) {
    await fetch(`api/todos/status/${id}`, {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Cotent-Type": "application/json,",
      },
    });
  }

  addItem(e) {}
  async componentDidMount() {
    const response = await fetch("/api/todos");
    const body = await response.json();
    this.setState({ items: body, isLoading: false });
  }
  handleChange(event) {
    this.setState({ title: event.target.value });
  }
  async remove(id) {
    await fetch(`api/todos/${id}`, {
      method: "DELETE",
      headers: {
        Accept: "application/json",
        "Cotent-Type": "application/json,",
      },
    }).then(() => {
      let updatedItems = [...this.state.items].filter((i) => i.id !== id);
      this.setState({ items: updatedItems });
    });
  }
  render() {
    const { items, isLoading } = this.state;
    if (isLoading)
      return (
        <div>
          <Navigation />
          <Container className="d-flex flex-row justify-content-center align-items-center">
            {" "}
            <Spinner />
          </Container>
        </div>
      );
    let rows = items.map((item) => (
      <div className="card-group w-70">
        <div className="card-body  bd-highlight" key={item.id}>
          <div className="card-title">
            {item.title}{" "}
            <span>
              {item.status && <Badge color="success">Завршено</Badge>}
              {!item.status && <Badge color="info">Не завршено</Badge>}
            </span>
          </div>
        </div>

        <div className="btn-group text-right w-0.5">
          <Button className="btn bg-transparent  ">✔</Button>
          <Button
            onClick={() => this.remove(item.id)}
            className="btn bg-transparent "
          >
            ❌
          </Button>
        </div>
      </div>
    ));
    return (
      <>
        <Navigation></Navigation>
        <Container>
          <h3>Додади TODO </h3>
          <UncontrolledAlert color="info">
            Внесете ,сменете статус или избришете todo !
          </UncontrolledAlert>
          <Container className="input-group w-50">
            <Input
              type="text"
              onChange={this.handleChange}
              placeholder="Впиши текст"
            />
            <Button onClick={this.addItem} outline color="success">
              Внеси
            </Button>{" "}
          </Container>
          {rows}
        </Container>
      </>
    );
  }
}
