import React, { Component } from "react";
import Navigation from "./Navigation";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { Container, Form, FormGroup, Button, Input } from "reactstrap";
import { Link } from "react-router-dom";
export default class Bill extends Component {
  state = {
    date: new Date(),
    isLoading: true,
    bills: [],
    Categories: [],
  };
  async componentDidMount() {
    const response = await fetch("api/categories");
    const body = await response.json();
    this.setState({ bills: body, isLoading: false });
  }

  async componentDidMount() {
    const response = await fetch("api/categories");
    const body = await response.json();
    this.setState({ Categories: body, isLoading: false });
  }

  render() {
    const title = <h3>Add Bill</h3>;
    const { bills, isLoading } = this.state;

    if (isLoading) return <div>Loading...</div>;

    return (
      <div>
        <Navigation />
        <Container>
          <Form>
            {title}
            <FormGroup>
              <label for="title">Title</label>
              <Input
                type="text"
                name="title"
                id="title"
                onChange={this.handleChange}
              />
            </FormGroup>
            <FormGroup>
              <label for="category">Category</label>
              {Categories.map((category) => (
                <div id={category.id}>{category.name}</div>
              ))}
              <Input
                type="text"
                name="category"
                id="category"
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <label for="date">Date</label>
              <DatePicker
                selected={this.state.date}
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <label for="price">Price</label>
              <Input
                type="text"
                name="price"
                id="price"
                type="number"
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <Button color="primary" type="submit">
                Save
              </Button>{" "}
              <Button color="secondary" tag={Link} to="/categories">
                Cancel
              </Button>{" "}
            </FormGroup>
          </Form>
        </Container>
      </div>
    );
  }
}
