import React, { Component } from "react";
import Navigation from "./Navigation";
import { UncontrolledAlert, Input, Button, Row, Container } from "reactstrap";
export default class Todo extends Component {
  constructor(props) {
    super(props);
    this.state = {
      Todos: [],
    };
    this.addItem = this.addItem.bind(this);
  }
  addItem(e) {}
  async componentDidMount() {
    const response = await fetch("/api/todos");
    const body = await response.json();
    this.setState({ Categories: body, isLoading: false });
  }
  render() {
    return (
      <>
        <Navigation></Navigation>
        <Container>
          <h3>Додади TODO </h3>
          <UncontrolledAlert color="info">
            Внесете,изменете или избришете todo листа!
          </UncontrolledAlert>
          <Row>
            <Input className="w-50" type="text" placeholder="Впиши текст" />
            <Button onClick={this.addItem} outline color="success">
              Внеси
            </Button>{" "}
          </Row>
        </Container>
      </>
    );
  }
}
