import React, { Component } from "react";
import AppNav from "./AppNav";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import "./App.css";
import {
  Table,
  Container,
  Input,
  Button,
  Label,
  FormGroup,
  Form,
} from "reactstrap";
import { Link } from "react-router-dom";
import Moment from "react-moment";

export default class Bill extends Component {
  // {
  //   "id": 100,
  //   "billdate": "2019-06-16T17:00:00Z",
  //   "description": "New York Business Trip",
  //   "price": "New York",
  //   "category": {
  //   "id": 1,
  //   "name": "Travel"
  //   }
  //   },

  emptyItem = {
    description: "",
    billdate: new Date(),
    id: 104,
    price: "",
    category: { id: 1, name: "Travel" },
  };

  constructor(props) {
    super(props);

    this.state = {
      isLoading: false,
      Categories: [],
      Expsenses: [],
      date: new Date(),
      item: this.emptyItem,
    };

    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleDateChange = this.handleDateChange.bind(this);
  }

  async handleSubmit(event) {
    const item = this.state.item;

    await fetch(`/api/bills`, {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(item),
    });

    event.preventDefault();
    this.props.history.push("/bills");
  }

  handleChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    let item = { ...this.state.item };
    item[name] = value;
    this.setState({ item });
    console.log(item);
  }

  handleDateChange(date) {
    let item = { ...this.state.item };
    item.billdate = date;
    this.setState({ item });
  }

  async remove(id) {
    await fetch(`/api/bills/${id}`, {
      method: "DELETE",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
    }).then(() => {
      let updatedBills = [...this.state.Expsenses].filter((i) => i.id !== id);
      this.setState({ Expsenses: updatedBills });
    });
  }

  async componentDidMount() {
    const response = await fetch("/api/categories");
    const body = await response.json();
    this.setState({ Categories: body, isLoading: false });

    const responseExp = await fetch("/api/bills");
    const bodyExp = await responseExp.json();
    this.setState({ Expsenses: bodyExp, isLoading: false });
    console.log(bodyExp);
  }

  render() {
    const title = <h3>Add bill</h3>;
    const { Categories } = this.state;
    const { Expsenses, isLoading } = this.state;

    if (isLoading) return <div>Loading....</div>;

    let optionList = Categories.map((category) => (
      <option value={category.id} key={category.id}>
        {category.name}
      </option>
    ));

    let rows = Expsenses.map((bill) => (
      <tr key={bill.id}>
        <td>{bill.description}</td>
        <td>{bill.price}</td>
        <td>
          <Moment date={bill.billdate} format="YYYY/MM/DD" />
        </td>
        <td>{bill.category.name}</td>
        <td>
          <Button size="sm" color="danger" onClick={() => this.remove(bill.id)}>
            Delete
          </Button>
        </td>
      </tr>
    ));

    return (
      <div>
        <AppNav />
        <Container>
          {title}

          <Form onSubmit={this.handleSubmit}>
            <FormGroup>
              <Label for="description">Title</Label>
              <Input
                type="description"
                name="description"
                id="description"
                onChange={this.handleChange}
                autoComplete="name"
              />
            </FormGroup>

            <FormGroup>
              <Label for="category">Category</Label>
              <select onChange={this.handleChange}>{optionList}</select>
            </FormGroup>

            <FormGroup>
              <Label for="city">Date</Label>
              <DatePicker
                selected={this.state.item.billdate}
                onChange={this.handleDateChange}
              />
            </FormGroup>

            <div className="row">
              <FormGroup className="col-md-4 mb-3">
                <Label for="price">price</Label>
                <Input
                  type="number"
                  name="price"
                  id="price"
                  onChange={this.handleChange}
                />
              </FormGroup>
            </div>
            <FormGroup>
              <Button color="primary" type="submit">
                Save
              </Button>{" "}
              <Button color="secondary" tag={Link} to="/">
                Cancel
              </Button>
            </FormGroup>
          </Form>
        </Container>
        {""}
        <Container>
          <h3>bill List</h3>
          <Table className="mt-4">
            <thead>
              <tr>
                <th width="30%">Description</th>
                <th width="10%">price</th>
                <th> Date</th>
                <th> Category</th>
                <th width="10%">Action</th>
              </tr>
            </thead>
            <tbody>{rows}</tbody>
          </Table>
        </Container>
      </div>
    );
  }
}
